  return (
    <div className='min-h-[550px]'>
        <h2 className='text-gray-900 text-xl font-semibold'>Корзина</h2>
        <div className='flex justify-between'>
            <div className='w-[633px]'>
                {carts.map((item, key) => 
                    <CartItem key={key} data={item}/>
