import { createSlice } from "@reduxjs/toolkit";


const initialState = {
    items: localStorage.getItem("carts") ? JSON.parse(localStorage.getItem("carts")) : [],
    statusTab: false,
    totalSum: localStorage.getItem("total") ? JSON.parse(localStorage.getItem("total")) : 0
}

const cartSlice = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        addToCart(state, action){
            const {productId, quantity} = action.payload;
            const indexProductId = (state.items).findIndex(item => item.productId === productId);
            if(indexProductId >= 0){
                state.items[indexProductId].quantity += quantity;
            }else{
                state.items.push({productId, quantity});
            }
            localStorage.setItem("carts", JSON.stringify(state.items));

        },
        changeQuantity(state, action){
            const {productId, quantity} = action.payload;
            const indexProductId = (state.items).findIndex(item => item.productId === productId);

            if(quantity > 0){
                state.items[indexProductId].quantity = quantity;
            }else{
                state.items = (state.items).filter(item => item.productId !== productId);
            }
            localStorage.setItem("carts", JSON.stringify(state.items));
        },
        changeTotal(state, action){
            const products = action.payload;
            let indexProductId;
            let totalSum = 0;
            for (const product in products){
                indexProductId = (state.items).findIndex(item => item.productId === product.id);
                totalSum += product.price*state.items[indexProductId].quantity;
                console.log(product.price);
            }
            localStorage.setItem("total", JSON.stringify(totalSum));

            return totalSum;
        }
    }
})
export const { addToCart, changeQuantity, changeTotal} = cartSlice.actions;
export default cartSlice.reducer;
